import React, { useState, useRef } from 'react';
import { useNavigate } from 'react-router-dom';
import { motion } from 'framer-motion';
import toast from 'react-hot-toast';
import { tournamentAPI } from '../../services/api';
import {
  ArrowLeftIcon,
  TrophyIcon,
  MapPinIcon,
  CalendarIcon,
  DocumentTextIcon,
  PhotoIcon,
} from '@heroicons/react/24/outline';

const TournamentCreate = () => {
  const navigate = useNavigate();
  const fileInputRef = useRef(null);
  
  const [formData, setFormData] = useState({
    name: '',
    location: '',
    start_date: '',
    end_date: '',
    description: ''
  });
  const [banner, setBanner] = useState(null);
  const [preview, setPreview] = useState(null);
  const [loading, setLoading] = useState(false);

  const handleImageChange = (e) => {
    const file = e.target.files[0];
    if (file) {
      setBanner(file);
      setPreview(URL.createObjectURL(file));
    }
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    
    if (!formData.name || !formData.start_date || !formData.end_date) {
      toast.error('Please fill in all required fields');
      return;
    }

    if (new Date(formData.end_date) < new Date(formData.start_date)) {
      toast.error('End date must be after start date');
      return;
    }

    setLoading(true);

    try {
      const submitData = new FormData();
      Object.keys(formData).forEach(key => {
        if (formData[key]) {
          submitData.append(key, formData[key]);
        }
      });
      if (banner) {
        submitData.append('banner_image', banner);
      }

      const response = await tournamentAPI.create(submitData);
      toast.success('Tournament created successfully!');
      navigate(`/organizer/tournaments/${response.data.tournament.id}`);
    } catch (error) {
      toast.error(error.response?.data?.message || 'Failed to create tournament');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="max-w-3xl mx-auto space-y-6">
      {/* Back Button */}
      <button
        onClick={() => navigate('/organizer/tournaments')}
        className="flex items-center gap-2 text-slate-600 hover:text-forest-600 transition-colors"
      >
        <ArrowLeftIcon className="w-5 h-5" />
        <span>Back to Tournaments</span>
      </button>

      {/* Header */}
      <motion.div
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
      >
        <h1 className="text-2xl font-bold text-slate-800">Create New Tournament</h1>
        <p className="text-slate-500 mt-1">
          Set up your fishing tournament details
        </p>
      </motion.div>

      {/* Form */}
      <motion.form
        initial={{ opacity: 0, y: 20 }}
        animate={{ opacity: 1, y: 0 }}
        transition={{ delay: 0.1 }}
        onSubmit={handleSubmit}
        className="card p-6 space-y-6"
      >
        {/* Banner Image */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-2">
            Tournament Banner (Optional)
          </label>
          <input
            type="file"
            ref={fileInputRef}
            onChange={handleImageChange}
            accept="image/*"
            className="hidden"
          />
          
          {preview ? (
            <div className="relative">
              <img
                src={preview}
                alt="Preview"
                className="w-full aspect-[3/1] object-cover rounded-xl"
              />
              <button
                type="button"
                onClick={() => fileInputRef.current?.click()}
                className="absolute bottom-4 right-4 px-4 py-2 bg-white/90 rounded-lg text-sm font-medium hover:bg-white transition-colors"
              >
                Change Banner
              </button>
            </div>
          ) : (
            <button
              type="button"
              onClick={() => fileInputRef.current?.click()}
              className="w-full aspect-[3/1] border-2 border-dashed border-slate-300 rounded-xl flex flex-col items-center justify-center hover:border-forest-500 hover:bg-forest-50/50 transition-colors group"
            >
              <PhotoIcon className="w-12 h-12 text-slate-400 group-hover:text-forest-500 mb-2" />
              <p className="text-slate-600 font-medium">Click to upload banner</p>
              <p className="text-slate-400 text-sm">Recommended: 1200 x 400 pixels</p>
            </button>
          )}
        </div>

        {/* Tournament Name */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-2">
            Tournament Name <span className="text-red-500">*</span>
          </label>
          <div className="relative">
            <TrophyIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
            <input
              type="text"
              value={formData.name}
              onChange={(e) => setFormData({ ...formData, name: e.target.value })}
              className="input-field pl-12"
              placeholder="e.g., Spring Fishing Championship 2024"
              required
            />
          </div>
        </div>

        {/* Location */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-2">
            Location
          </label>
          <div className="relative">
            <MapPinIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
            <input
              type="text"
              value={formData.location}
              onChange={(e) => setFormData({ ...formData, location: e.target.value })}
              className="input-field pl-12"
              placeholder="e.g., Lake Toba, North Sumatra"
            />
          </div>
        </div>

        {/* Dates */}
        <div className="grid md:grid-cols-2 gap-4">
          <div>
            <label className="block text-sm font-medium text-slate-700 mb-2">
              Start Date <span className="text-red-500">*</span>
            </label>
            <div className="relative">
              <CalendarIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
              <input
                type="date"
                value={formData.start_date}
                onChange={(e) => setFormData({ ...formData, start_date: e.target.value })}
                className="input-field pl-12"
                required
              />
            </div>
          </div>
          <div>
            <label className="block text-sm font-medium text-slate-700 mb-2">
              End Date <span className="text-red-500">*</span>
            </label>
            <div className="relative">
              <CalendarIcon className="absolute left-4 top-1/2 -translate-y-1/2 w-5 h-5 text-slate-400" />
              <input
                type="date"
                value={formData.end_date}
                onChange={(e) => setFormData({ ...formData, end_date: e.target.value })}
                className="input-field pl-12"
                required
              />
            </div>
          </div>
        </div>

        {/* Description */}
        <div>
          <label className="block text-sm font-medium text-slate-700 mb-2">
            Description
          </label>
          <textarea
            value={formData.description}
            onChange={(e) => setFormData({ ...formData, description: e.target.value })}
            className="input-field min-h-[120px]"
            placeholder="Describe your tournament, rules, prizes, etc."
          />
        </div>

        {/* Info */}
        <div className="bg-forest-50 rounded-xl p-4 border border-forest-200">
          <h3 className="font-medium text-forest-800 mb-2">ðŸ“‹ What's Next?</h3>
          <p className="text-forest-700 text-sm">
            After creating the tournament, you'll be able to:
          </p>
          <ul className="text-forest-700 text-sm mt-2 space-y-1">
            <li>â€¢ Add ponds and configure fishing zones/areas</li>
            <li>â€¢ Set pricing for each area</li>
            <li>â€¢ Get shareable registration and leaderboard links</li>
            <li>â€¢ Manage participant registrations</li>
          </ul>
        </div>

        {/* Submit */}
        <div className="flex gap-4">
          <button
            type="button"
            onClick={() => navigate('/organizer/tournaments')}
            className="btn-secondary flex-1"
          >
            Cancel
          </button>
          <button
            type="submit"
            disabled={loading}
            className="btn-success flex-1 flex items-center justify-center gap-2"
          >
            {loading ? (
              <div className="w-5 h-5 border-2 border-white/30 border-t-white rounded-full animate-spin" />
            ) : (
              <>
                <TrophyIcon className="w-5 h-5" />
                Create Tournament
              </>
            )}
          </button>
        </div>
      </motion.form>
    </div>
  );
};

export default TournamentCreate;
